
public class numone {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 10;
		int y = 4;
		System.out.print(" x = ");
		System.out.println(x);
		System.out.print(" y = ");
		System.out.println(y);
		System.out.print(" x + y = ");
		System.out.println(x+y);
		System.out.print(" x - y = ");
		System.out.println(x-y);
		System.out.print(" x * y = ");
		System.out.println(x*y);
		System.out.print(" x / y = ");
		System.out.println(x/y);		
	}

}
